int HandleIDCMP_About()
{
	struct IntuiMessage *m;
	int (*func)(void);
	BOOL running = TRUE;

	while(m=GT_GetIMsg(AboutWnd->UserPort))
	{
		CopyMem((APTR)m,(APTR)&AboutMsg,sizeof(struct IntuiMessage));
		GT_ReplyIMsg(m);
		switch(AboutMsg.Class)
		{
			case IDCMP_CLOSEWINDOW:
				running=FN_CloseWindow_About();
				if(running > 1) return running;
				break;
			case IDCMP_GADGETUP:
				func=(int (*)(void))(( struct Gadget * )AboutMsg.IAddress )->UserData;
				running=func();
				if(running > 1) return running;
				break;
		}
	}
	return(running);
}

