int HandleIDCMP_Properties()
{
	struct IntuiMessage *m;
	struct MenuItem *n;
	int (*func)(void);
	BOOL running = TRUE;

	while(m=GT_GetIMsg(PropertiesWnd->UserPort))
	{
		CopyMem((APTR)m,(APTR)&PropertiesMsg,sizeof(struct IntuiMessage));
		GT_ReplyIMsg(m);
		switch(PropertiesMsg.Class)
		{
			case IDCMP_MOUSEMOVE:
				running=FN_MouseMove_Properties();
				if(running > 1) return running;
				break;
			case IDCMP_INTUITICKS:
				running=FN_IntuiTicks_Properties();
				if(running > 1) return running;
				break;
			case IDCMP_CHANGEWINDOW:
				running=FN_ChangeWindow_Properties();
				if(running > 1) return running;
				break;
			case IDCMP_CLOSEWINDOW:
				running=FN_CloseWindow_Properties();
				if(running > 1) return running;
				break;
			case IDCMP_INACTIVEWINDOW:
				running=FN_InActiveWindow_Properties();
				if(running > 1) return running;
				break;
			case IDCMP_MENUPICK:
				while(PropertiesMsg.Code!=MENUNULL)
				{
					n=ItemAddress(MainMenus,PropertiesMsg.Code);
					func=(int (*)(void))(GTMENUITEM_USERDATA(n));
					running=func();
					if(running > 1) return running;
					PropertiesMsg.Code=n->NextSelect;
				}
				break;
			case IDCMP_GADGETUP:
			case IDCMP_GADGETDOWN:
				func=(int (*)(void))(( struct Gadget * )PropertiesMsg.IAddress )->UserData;
				running=func();
				if(running > 1) return running;
				break;
		}
	}
	return(running);
}

