int HandleIDCMP_Status()
{
	struct IntuiMessage *m;
	struct MenuItem *n;
	int (*func)(void);
	BOOL running = TRUE;

	while(m=GT_GetIMsg(StatusWnd->UserPort))
	{
		CopyMem((APTR)m,(APTR)&StatusMsg,sizeof(struct IntuiMessage));
		GT_ReplyIMsg(m);
		switch(StatusMsg.Class)
		{
			case IDCMP_CHANGEWINDOW:
				running=FN_ChangeWindow_Status();
				if(running > 1) return running;
				break;
			case IDCMP_MENUPICK:
				while(StatusMsg.Code!=MENUNULL)
				{
					n=ItemAddress(MainMenus,StatusMsg.Code);
					func=(int (*)(void))(GTMENUITEM_USERDATA(n));
					running=func();
					if(running > 1) return running;
					StatusMsg.Code=n->NextSelect;
				}
				break;
		}
	}
	return(running);
}

